/* Problem 1083 - The Fair Nut and Rectangles -Codeforces O(NlogN)** -The lines are inserted in sorted order of slope* -The query positions are made in ARBITRARY order**/struct line{    ll m, n;    ll eval(ll x){return m*x+n;}    ll div(ll x, ll y){return x/y - ((x^y)<0 && x%y);}    ll interX(line &l){return div(l.n-n, m-l.m);}};const int MAXN = 1e6+5;lll a[MAXN];//mainsort(a, a+n);deque<line> hull;auto ternary_search = [&hull](ll x){    int l = 0, r = sz(hull)-1;    while(r-l >= 3){        int m1 = l + (r-l)/3;        int m2 = r - (r-l)/3;        ll f1 = hull[m1].eval(x);        ll f2 = hull[m2].eval(x);        if(f1 < f2){            l = m1;        }else{            r = m2;        }    }    ll ans = LONG_LONG_MIN;    for (int i=l; i<=r; i++) ans = max(ans, hull[i].eval(x));    return ans;};hull.push_front({0, 0});ll ans=0;for (int i=0; i<n; i++){    ll tmp = 1LL * a[i].p * a[i].q - a[i].v + ternary_search(a[i].q);    ans = max(ans, tmp);    line cur = {-a[i].p, tmp};    while(sz(hull) >=2 && cur.interX(hull[0]) >= hull[0].interX(hull[1]))        hull.pop_front();    hull.push_front(cur);}       