/* Problem 1083 - The Fair Nut and Rectangles -Codeforces O(N)** -The lines are inserted in sorted order of slope* -The query positions are made in sorted order**/struct line{    ll m, n;    ll eval(ll x){return m*x+n;}    ll div(ll x, ll y){return x/y - ((x^y)<0 && x%y);}    ll interX(line &l){return div(l.n-n, m-l.m);}};const int MAXN = 1e6+5;lll a[MAXN];//mainsort(a, a+n);deque<line> hull;hull.push_front({0, 0});ll ans=0;for (int i=0; i<n; i++){    while(sz(hull) >= 2 && hull[sz(hull)-1].eval(a[i].q) <= hull[sz(hull)-2].eval(a[i].q))        hull.pop_back();    ll tmp = 1LL * a[i].p * a[i].q - a[i].v + hull.back().eval(a[i].q);    ans = max(ans, tmp);    line cur = {-a[i].p, tmp};    while(sz(hull) >=2 && cur.interX(hull[0]) >= hull[0].interX(hull[1]))        hull.pop_front();    hull.push_front(cur);}       