char str[MAXN];int rad[2*MAXN];void Manacher(){    int i,j,k,n = strlen(str);    for(i = 0, j = 0; i < 2*n-1; i+=k)    {        while(i-j>=0 && i+j+1<2*n && str[(i-j)/2] == str[(i+j+1)/2])        {            j++;        }        rad[i] = j;        for(k = 1; k <= rad[i] && rad[i-k] != rad[i] - k; k++)        {            rad[i+k] = min(rad[i-k], rad[i]-k);        }        j = max(j-k, 0);    }}